window.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll(".header__link");for(let t of e)t.addEventListener("click",(function(e){e.preventDefault();const o=t.getAttribute("href");document.querySelector(o).scrollIntoView({behavior:"smooth",block:"start"})}));let t=document.querySelector(".menu__icon"),o=document.querySelector("body");if(t){let e=document.querySelector(".nav");t.addEventListener("click",(()=>{o.classList.toggle("lock"),t.classList.toggle("_active-icon"),e.classList.toggle("_active")}))}}));